mapper.select:
SELECT 

FROM act_lottery a

WHERE a.id = #{id}

SELECT 

FROM act_lottery a

WHERE a.recieve_code = #{recieveCode}

SELECT 

FROM act_lottery a




		AND a.activity = #{activity}


		AND a.user_id = #{userId}




		ORDER BY ${page.orderBy}

SELECT 
	count(*)
FROM act_lottery a



		AND a.activity = #{activity}


		AND a.user_id = #{userId}


		AND a.openId = #{openid}


		AND a.customer_id = #{customerId}


		AND (

			a.user_id = #{userId}


			OR a.openId = #{openid}


			OR a.customer_id = #{customerId}

		)


		AND a.create_time >= #{timeStart}


		AND a.create_time <= #{timeEnd}

		select *
		  from (select row_.*, rownum rownum_
		          from (
						select 
                           u.nick_name as "showUserName",
	                       pt.name      as "prizeName"
	                    from act_lottery a
	                    left join act_prize p
	                      on a.prize = p.id
	                    left join act_prize_type pt
	                      on p.code = pt.code
	                    left join wechat_user u
	                      on a.openid = u.open_id

							a.is_hit = 1 and a.prize is not null

								AND a.activity = #{activitycode}


	                   order by a.id desc
				    	) row_
			         WHERE  rownum <= #{end} 
	        ) 
		 WHERE rownum_ > #{start}

mapper.update:
UPDATE act_lottery SET 	
	user_id = #{userId,jdbcType=VARCHAR},
	openid = #{openid,jdbcType=VARCHAR},
	customer_id = #{customerId,jdbcType=VARCHAR},
	has_recieve = #{hasRecieve}
WHERE id = #{id}
