mapper.select:
SELECT 

FROM act_message a
WHERE a.id = #{id}

SELECT 

FROM act_message a


		AND a.act_code = #{actCode}


		AND a.user_id = #{userId}


		AND a.status = #{status}


		AND a.content LIKE 
			'%'||#{content}||'%'
			'%'+#{content}+'%'
			concat('%',#{content},'%')

SELECT * FROM ( SELECT
,b.nick_name AS "nickName" ,b.portrait AS "headImageUrl" FROM act_message a LEFT JOIN acc_user b ON b.user_id = a.user_id
AND a.status = 1
AND a.act_code = #{activityCode}
AND a.id > #{maxOrder}
ORDER BY a.id ASC) t
WHERE rownum <= #{maxLength}
