mapper.select:
SELECT 

FROM act_share a

WHERE a.id = #{id}

SELECT 
	count(*)
FROM act_share a



		AND a.type = #{type}


		AND a.activity = #{activity}


		AND a.user_id = #{userId}


		AND (

			a.user_id = #{userId}


			OR a.openId = #{openid}


			OR a.customer_id = #{customerId}

		)


		AND a.create_time >= #{timeStart}


		AND a.create_time <= #{timeEnd}

SELECT 

FROM act_share a



		AND a.type = #{type}


		AND a.activity = #{activity}


		AND a.user_id = #{userId}


		AND a.create_time >= #{timeStart}


		AND a.create_time <= #{timeEnd}


		AND a.is_use = #{isUse}
