mapper.select:
SELECT 

FROM act_check_in_opportunity a

WHERE a.id = #{id}

SELECT 

FROM act_check_in_opportunity a




		AND a.user_id = #{userId}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}

SELECT 

FROM act_check_in_opportunity a



		AND a.openid = #{openid}


		AND a.status >= #{status}


		AND a.create_time >= #{timeStart}


		AND a.create_time <= #{timeEnd}

SELECT 

FROM act_check_in_opportunity a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE act_check_in_opportunity SET 
	status = #{status},
	use_time = #{useTime, jdbcType=TIMESTAMP},
	remark = #{remark, jdbcType=VARCHAR}
WHERE id = #{id}
