mapper.select:
SELECT 

FROM act_check_in_prize a

WHERE a.id = #{id}

SELECT 

FROM act_check_in_prize a



		AND a.openid = #{openid}


		AND a.create_time >= #{timeStart}


		AND a.create_time <= #{timeEnd}

SELECT 

FROM act_check_in_prize a




		AND a.user_id = #{userId}


		AND a.openid = #{openid}


order by a.create_time desc

SELECT 

FROM act_check_in_prize a






		ORDER BY ${page.orderBy}
