mapper.select:
SELECT 

FROM act_guess_51 a

WHERE a.id = #{id}

select MAX(decode(a.direction, '1', NVL(sum(a.use_point),0), null)) as "upPoint",
       MAX(decode(a.direction, '0', NVL(sum(a.use_point),0), null)) as "downPoint"
  from act_guess_51 a
 where a.guess_date =  #{guessDate}
  and a.guess_no =  #{guessNo}
 group by a.direction

		select MAX(decode(a.direction, '1', count(a.id))) as "upCount",
           MAX(decode(a.direction, '0', count(a.id))) as "downCount"
	     from act_guess_51 a
		 where a.guess_date =  #{guessDate}
		  and a.guess_no =  #{guessNo}
	     group by a.direction

select *
  from (select row_.*, rownum rownum_
          from (
			SELECT 

			FROM act_guess_51 a

			where
				 a.result_direction is null
				and a.guess_date = #{guessDate}
				and a.guess_no = #{guessNo}
			order by a.id
		) row_
         WHERE  rownum <= #{end} 
        ) 
 WHERE rownum_ > #{start}

select *
  from (select row_.*, rownum rownum_
          from (
				SELECT 

				FROM act_guess_51 a
				where
				 a.user_id = #{userId}
				order by a.id desc
			) row_
		         WHERE  rownum <= #{end} 
        ) 
 WHERE rownum_ > #{start}

select *
  from (select row_.*, rownum rownum_
          from (
			SELECT 

			FROM act_guess_51 a

			where
				 a.user_id = #{userId}
				 and a.create_time < #{createTime} 
			order by a.create_time desc
		) row_
        ) 
 WHERE rownum_ = 1

SELECT 

FROM act_guess_51 a



		AND a.user_id = #{userId}


		and a.guess_date = #{guessDate}


		and a.guess_no = #{guessNo}
