mapper.select:
SELECT 

FROM act_level2_recieve_invite a

WHERE a.id = #{id}

SELECT 

FROM act_level2_recieve_invite a



		AND a.user_id = #{userId}


		AND a.user_mobile = #{userMobile}


		AND a.invite_mobile = #{inviteMobile}


		AND a.status = #{status}


		AND a.create_time BETWEEN #{createTimeBegin} AND #{createTimeEnd}

select *
  from (select row_.*, rownum rownum_
          from (
		SELECT 

		FROM act_level2_recieve_invite a

		where
			a.status = 1

				AND a.invite_mobile = #{inviteMobile}

			order by a.id
			) row_
         WHERE  rownum <= #{end} 
        ) 
 WHERE rownum_ > #{start}
