mapper.select:
SELECT 

FROM act_level2_recieve_user a

WHERE a.id = #{id}

select * from (
	SELECT 

	FROM act_level2_recieve_user a

	WHERE a.type = #{type}
	and a.mobile = #{mobile}
	order by a.effective_date desc
)
where rownum = 1

SELECT 

FROM act_level2_recieve_user a




		AND a.custmer_id = #{custmerId}


		AND a.mobile = #{mobile}


		AND a.type = #{type}


		AND a.effective_date = #{effectiveDate}


		AND a.effective_date BETWEEN #{effectiveDateBegin} AND #{effectiveDateEnd}

SELECT 
	a.mobile as "mobile",
	a.effective_date AS "effectiveDate"
FROM act_level2_recieve_user a



		 a.effective_date >= #{effectiveDateBegin}
		AND a.effective_date <= #{effectiveDateEnd}
