mapper.select:
SELECT 

FROM act_activity_prize a

WHERE a.id = #{id}

SELECT , b.status AS "redeemStatus", b.code AS "redeemCode", c.name AS "typeName", d.goods_name as "goodsName", d.goods_time as "goodsTime" FROM act_activity_prize a LEFT JOIN act_redeem b ON a.redeem_id = b.id LEFT JOIN act_redeem_type c ON b.type_code = c.code LEFT JOIN act_activity_goods d ON a.activity_code = d.activity_code AND b.goods_id = d.goods_id
AND a.activity_code = #{activityCode}
AND a.user_id = #{userId}
AND b.status = #{redeemStatus}

mapper.update:
UPDATE act_activity_prize SET 	
	activity_code = #{activityCode, jdbcType=VARCHAR},
	user_id = #{userId, jdbcType=VARCHAR},
	redeem_id = #{redeemId, jdbcType=INTEGER},
	create_time = #{createTime, jdbcType=TIMESTAMP}
WHERE id = #{id}
