mapper.select:
SELECT 

FROM act_turntable_prize a

WHERE a.id = #{id}

	select *
	  from (select row_.*, rownum rownum_
	          from (
					select NVL(u.nick_name,REPLACE(u.mobile, substr(u.mobile,4,4), '****')) as "mobile",
		                   p.name      as "prizeName",
		                   a.type as "type",
		                   a.worth as "worth"
		              from act_turntable_prize a
		              left join act_prize_type p
		                on a.prize_code = p.code
		              left join acc_user u
		                on a.user_id = u.user_id
		              left join act_prize ap
        				on a.prize_id = ap.id


							AND p.activity_code = #{activity}


		             order by a.id desc
			    	) row_
		         WHERE  rownum <= #{end} 

        ) 
	 WHERE rownum_ > #{start}

	select *
	  from (select row_.*, rownum rownum_
	          from (
					select a.type as "type",
		                 t.time as "time",
		                 a.worth as "worth",
		                 p.redeem_code as "redeemCode",
		                 p.card_no as "cardNo",
		                 p.card_password as "cardPassword",
		                 p.status as "status",
		                 a.create_time as "createTime"
		            from act_turntable_prize a
		            left join act_prize_type t
		              on a.prize_code = t.code
		            left join act_prize p
		              on a.prize_id = p.id
				    where a.user_id = #{userId}

							AND p.activity_code = #{activity}

				 order by a.id desc
			) row_
		         WHERE  rownum <= #{end} 
        ) 
	 WHERE rownum_ > #{start}

SELECT 

FROM act_turntable_prize a



		AND a.user_id = #{userId}




		ORDER BY ${page.orderBy}

SELECT 

FROM act_turntable_prize a






		ORDER BY ${page.orderBy}
