mapper.select:
SELECT 

FROM act_agreement_ref a

WHERE a.id = #{id}

SELECT 

FROM act_agreement_ref a



		AND a.activity_code = #{activityCode}


		AND a.product_code = #{productCode}


ORDER BY a.agreement_id ASC

mapper.update:
DELETE FROM act_agreement_ref
WHERE product_code = #{productCode}
AND activity_code = #{activityCode}
