mapper.select:
SELECT 

FROM act_task a



		AND a.id = #{id}


		AND a.activity_code = #{activityCode}


		AND a.code = #{code}


		AND a.status = #{status}

SELECT 

	,b.name AS "activityName"
	,c.name AS "goodsName"
FROM act_task a
LEFT JOIN act_activity b ON a.activity_code = b.code
LEFT JOIN act_task_goods c ON a.goods_code = c.code



		AND a.activity_code = #{activityCode}


		AND a.code = #{code}


		AND a.code in

			#{item}



		AND a.name LIKE '%'||#{name}||'%'


		AND a.status = #{status}


ORDER BY a.sort asc nulls last, a.id desc

SELECT 

FROM act_task a






		ORDER BY ${page.orderBy}
